import os
from fastapi import FastAPI, Request, Response
from fastapi.staticfiles import StaticFiles
from fastapi.templating import Jinja2Templates

app = FastAPI()

SERVER_ROOT = os.path.dirname(__file__)

app.mount(
    "/static", StaticFiles(directory=os.path.join(SERVER_ROOT, "static")), name="static"
)

templates = Jinja2Templates(directory=os.path.join(SERVER_ROOT, "templates"))

# option for routing `@app.X` where `X` is one of
# post: to create data.
# get: to read data.
# put: to update data.
# delete: to delete data.

# `127.0.0.1:8000/docs` endpoint will have autogenerated docs for the written code

# Test Driven Development --> https://fastapi.tiangolo.com/tutorial/testing/

@app.get("/")
def landing(request: Request) -> Response:
    return templates.TemplateResponse("index.html", {"request": request})


@app.get("/some-endpoint")
def endpoint(request: Request) -> Response:
    return templates.TemplateResponse("different_view.html", {"request": request})


@app.get("/some-endpoint/{some_value}")
def endpoint_variable(request: Request, some_value: int) -> Response:
    return templates.TemplateResponse("different_view.html", {"request": request, "n_banana": some_value})

